import static com.jayway.jsonpath.JsonPath.parse

//Get the parsed json of GetSources test step response
def json = new groovy.json.JsonSlurper().parseText(context.expand('${GetCustomDataElements#Response}'))

//datasource
def CustomDataNameDatasource =context.expand( '${RewardProviderCustomAttributes#CustomAttribute}')

//CustomDataId, CustomDataName, EntityName
def getCustomDataIdByCustomAttribute = { code ->  json.find {it?.CustomDataName == CustomDataNameDatasource}?.CustomDataId }
def getCustomDataNameByCustomAttribute = { code ->  json.find {it?.CustomDataName == CustomDataNameDatasource}?.CustomDataName }
def getCustomDataEntityNameByCustomAttribute = { code ->  json.find {it?.CustomDataName == CustomDataNameDatasource}?.CustomDataEntityName}
def CustomDataId= (getCustomDataIdByCustomAttribute('CustomDataNameDatasource'))
def CustomDataName= (getCustomDataIdByCustomAttribute('CustomDataNameDatasource'))
def CustomDataEntityName= (getCustomDataIdByCustomAttribute('CustomDataNameDatasource'))


if (( CustomDataId == "" || CustomDataId== null ) ) 
   testRunner.gotoStepByName("AddCustomData")

else
   testRunner.gotoStepByName("CustomDataId")

