try
            {
                Epsilon.Fusion.Models.Batch.BatchJobModel model = JsonConvert.DeserializeObject<Epsilon.Fusion.Models.Batch.BatchJobModel>(input.ToString());
                if (model.JsonExternalData != null)
                {
                    dynamic xmlJson = verb.AccessExternalData(model);
                    if (xmlJson != null)
                    {
                        verb.WriteFile(xmlJson.FileName.ToString(), JsonConvert.DeserializeObject<dynamic>(xmlJson.FileContent.ToString()), xmlJson.FileLocation.ToString(), useHeaders: (bool)xmlJson.UseHeader);
                    }
                }

                //update Json to null
                DbParameterCollection parameters = new DbParameterCollection();
                parameters.Add(new DbParameter("BatchJobId", model.BatchJobId, DbType.Guid));

                verb.BeginDatabaseTransaction();

                string sql = string.Format("UPDATE T_SYS_JOB SET JSON_EXTERNAL_DATA = null WHERE SYS_JOB_ID = :BatchJobId");
                verb.ModifyDatabase(sql, parameters);

                verb.CommitDatabaseTransaction();
            }
            catch (Exception ex)
            {
                verb.RollbackDatabaseTransaction();
                throw new Exception(ex.Message);
            }

