import groovy.json.JsonGenerator
import groovy.json.JsonGenerator.Converter

def profileLayoutList = testRunner.testCase.getTestSteps().ProfileLayoutProperties.getPropertyList();
def addressLayoutList = testRunner.testCase.getTestSteps().AddressLayoutProperties.getPropertyList();
def emailLayoutList = testRunner.testCase.getTestSteps().EmailLayoutProperties.getPropertyList();
def phoneLayoutList = testRunner.testCase.getTestSteps().PhoneLayoutProperties.getPropertyList();
def preferredStoreLayoutList = testRunner.testCase.getTestSteps().PreferredStoreLayoutProperties.getPropertyList();   

def parseCollection(propertyList){  	
	def mapClass = [:]

	def RecordNumber = 00;

	for( item in propertyList)
	{
		if( item.value == null  ){
			 item.value= '';
		}
		if(item.name == 'RecordNumber')
		{
			RecordNumber =  item.value;
		}
		if (item.name == 'InteractionId')
		{
			 item.value = '0000';
		}
		if(item.name == 'UpdateFileId'){
			item.value = '1110';
		}
		if(item.name == 'UpdateRecordNumber'){
			item.value = '2220';
		}
		def activityDate = context.expand( '${#TestCase#ActivityDate}' )

		if (activityDate  == 'True' && item.name == 'ActivityDate')
		{
			 item.value =new Date().format('yyyy-MM-dd HH:mm:ss.SSS', TimeZone.getTimeZone('UTC'));
		}
		else if (activityDate  == 'False' && item.name == 'ActivityDate')
		{
			 item.value = '';
		}		

	    mapClass[item.name] = item.value;	  
	}  
		def enablePostingKey = context.expand( '${#TestCase#EnablePostingKey}' );
	 	 if(RecordNumber == '01' && enablePostingKey == 'True')
		{
			   mapClass['PostingKeyCode'] = 'PK1239';
				def postingKeyValue = context.expand( '${ProfileLayoutDataSource#PostingKeyValue}' )
			   mapClass['PostingKeyValue'] = postingKeyValue;
		}

	return mapClass;
}


def jsonDefaultOutput = new JsonGenerator.Options()
.dateFormat('MM-dd-yyyy')
.build(); 

def profileString = parseCollection(profileLayoutList);
def addressString = parseCollection(addressLayoutList);
def phoneString = parseCollection(phoneLayoutList);
def emailString = parseCollection(emailLayoutList);
def preferredStoreString =parseCollection(preferredStoreLayoutList);

def s3Bucket = context.expand( '${#Project#S3Bucket}' );
def todayDate = new Date().format( 'MM-dd-yyyy' );
def jobName = context.expand( '${ProfileLayoutDataSource#JobName}' );
def programCode = context.expand( '${#Project#ProgramCode}' )
def fileContent =  [profileString , addressString  , emailString , phoneString , preferredStoreString  ];
def scriptName = context.expand( '${#TestCase#ScriptName}' );

def jsonExternalDataMap = [:]
def dataHubProcessId = context.expand( '${RunExecuteBatchWorkFlowforProfileImport#DataHubProcessId}' );
def fileName = 'ProfileImport' + dataHubProcessId  + '.txt';
jsonExternalDataMap["FileName"]  = fileName;
jsonExternalDataMap["FileLocation"]  = "s3://" + s3Bucket + "/automation/ProcessProfileInput";
jsonExternalDataMap["FileContent"] = fileContent;
jsonExternalDataMap["UseHeader"] = 'false';

def batchInputJsonMap = [:];
batchInputJsonMap["JobName"] = 'InvokeScript_' + jobName;
batchInputJsonMap["FileName"] = fileName;
batchInputJsonMap["FileInputLocation"] = "s3://" + s3Bucket + "/automation/" + todayDate + "/Input";
batchInputJsonMap["FileOutputLocation"] = "s3://" + s3Bucket + "/automation/" + todayDate + "/Output";
batchInputJsonMap["EmailAddresses"]= "";
batchInputJsonMap["JobTypeId"]= "4923d185-0c86-4c15-9356-67597ab70526";
batchInputJsonMap["JobType"]= "";
batchInputJsonMap["ProgramCode"]= programCode;
batchInputJsonMap["DataOutputType"]= "FULL";
batchInputJsonMap["MaxThreads"]= 50;

batchInputJsonMap["ScriptName"] =  scriptName;
batchInputJsonMap["JsonExternalData"]= jsonExternalDataMap;
batchInputJsonMap["AdditionalRunData"]= "";

def batchInputJson = jsonDefaultOutput.toJson(batchInputJsonMap);

return batchInputJson;
