import groovy.json.JsonGenerator
import groovy.json.JsonGenerator.Converter

def storeImportLayoutList1 = testRunner.testCase.getTestSteps().StoreImportPropertiesRecord1.getPropertyList();
def storeImportLayoutList2 = testRunner.testCase.getTestSteps().StoreImportPropertiesRecord2.getPropertyList();
def storeImportLayoutList3 = testRunner.testCase.getTestSteps().StoreImportPropertiesRecord3.getPropertyList();
def storeImportLayoutList4 = testRunner.testCase.getTestSteps().StoreImportPropertiesRecord4.getPropertyList();

def parseCollection(propertyList){  	
	def mapClass = [:]
	for( item in propertyList)
	{
		if( item.value == null  ){
			 item.value= '';
		}
	    mapClass[item.name] = item.value;	  
	}    
	return mapClass;
}

def jsonDefaultOutput = new JsonGenerator.Options()
.dateFormat('MM-dd-yyyy')
.build(); 

def storeImportString1 = parseCollection(storeImportLayoutList1);
def storeImportString2 = parseCollection(storeImportLayoutList2);
def storeImportString3 = parseCollection(storeImportLayoutList3);
def storeImportString4 = parseCollection(storeImportLayoutList4);

def s3Bucket = context.expand( '${#Project#S3Bucket}' )
def todayDate = new Date().format( 'MM-dd-yyyy' );
def jobName = context.expand( '${StoreImportLayoutDataSource#JobName}' )
def programCode = context.expand( '${#Project#ProgramCode}' )
def fileContent =  [storeImportString1, storeImportString2, storeImportString3, storeImportString4];
def scriptName = context.expand( '${#TestCase#ScriptName}' )

def jsonExternalDataMap = [:]
def dataHubProcessId = context.expand( '${RunExecuteBatchWorkFlowforStoreImport#DataHubProcessId}' )


def fileName = 'StoreImport' + dataHubProcessId  + '.txt';
jsonExternalDataMap["FileName"]  = fileName;
jsonExternalDataMap["FileLocation"]  = "s3://" + s3Bucket + "/automation/ProcessStoreInput";
jsonExternalDataMap["FileContent"] = fileContent;
jsonExternalDataMap["UseHeader"] = 'true';

def batchInputJsonMap = [:];
batchInputJsonMap["JobName"] = 'InvokeScript_' + jobName;
batchInputJsonMap["FileName"] = fileName;
batchInputJsonMap["FileInputLocation"] = "s3://" + s3Bucket + "/automation/" + todayDate + "/Input";
batchInputJsonMap["FileOutputLocation"] = "s3://" + s3Bucket + "/automation/" + todayDate + "/Output";
batchInputJsonMap["EmailAddresses"]= "";
batchInputJsonMap["JobTypeId"]= "4923d185-0c86-4c15-9356-67597ab70526";
batchInputJsonMap["JobType"]= "";
batchInputJsonMap["ProgramCode"]= programCode;
batchInputJsonMap["DataOutputType"]= "FULL";
batchInputJsonMap["MaxThreads"]= 50;

batchInputJsonMap["ScriptName"] =  scriptName;
batchInputJsonMap["JsonExternalData"]= jsonExternalDataMap;
batchInputJsonMap["AdditionalRunData"]= "";

def batchInputJson = jsonDefaultOutput.toJson(batchInputJsonMap);

return batchInputJson;
