import groovy.json.JsonGenerator
import groovy.json.JsonGenerator.Converter


def sysOfferTagImportLayoutList1 = testRunner.testCase.getTestSteps().SysOfferTagImportPropertiesRecord1.getPropertyList();
def sysOfferTagImportLayoutList2 = testRunner.testCase.getTestSteps().SysOfferTagImportPropertiesRecord2.getPropertyList();
def sysOfferTagImportLayoutList3 = testRunner.testCase.getTestSteps().SysOfferTagImportPropertiesRecord3.getPropertyList();
def sysOfferTagImportLayoutList4 = testRunner.testCase.getTestSteps().SysOfferTagImportPropertiesRecord4.getPropertyList();
def sysOfferTagImportLayoutList5 = testRunner.testCase.getTestSteps().SysOfferTagImportPropertiesRecord5.getPropertyList();

def parseCollection(propertyList){  	
	def mapClass = [:]
	for( item in propertyList)
	{
		if( item.value == null  ){
			 item.value= '';
		}
	    mapClass[item.name] = item.value;	  
	}    
	return mapClass;
}

def jsonDefaultOutput = new JsonGenerator.Options()
.dateFormat('MM-dd-yyyy')
.build(); 

def sysOfferTagImportString1 = parseCollection(sysOfferTagImportLayoutList1);
def sysOfferTagImportString2 = parseCollection(sysOfferTagImportLayoutList2);
def sysOfferTagImportString3 = parseCollection(sysOfferTagImportLayoutList3);
def sysOfferTagImportString4 = parseCollection(sysOfferTagImportLayoutList4);
def sysOfferTagImportString5 = parseCollection(sysOfferTagImportLayoutList5);

def s3Bucket = context.expand( '${#Project#S3Bucket}' )
def todayDate = new Date().format( 'MM-dd-yyyy' );
