def programCode = context.expand( '${#Project#ProgramCode}' )
def fileContent =  [sysOfferTagImportString1,sysOfferTagImportString2,sysOfferTagImportString3,sysOfferTagImportString4,sysOfferTagImportString5];
def scriptName = context.expand( '${#TestCase#ScriptName}' )

def jsonExternalDataMap = [:]
