con:config.script:
import java.text.SimpleDateFormat

// Gets the current date
def date = new Date()
def currentDate = new SimpleDateFormat("MM/dd/yyyy")

final Calendar calendar = Calendar.getInstance();
calendar.setTime(date);

// Add +1 day to the current date
calendar.add(Calendar.DAY_OF_YEAR, 1);
def nextDayDate = currentDate.format(calendar.getTime()); 
testRunner.testCase.testSuite.project.setPropertyValue( "NextDayDate", nextDayDate)

// Add +365 days to the current date
calendar.add(Calendar.DAY_OF_YEAR, 365);
def nextYearDate = currentDate.format(calendar.getTime()); 
testRunner.testCase.testSuite.project.setPropertyValue( "NextYearDate", nextYearDate)

def today = new Date()
def yesterday = today - 1;
def yesterdaysDate = yesterday.format("MM/dd/yyyy");
testRunner.testCase.testSuite.project.setPropertyValue( "YesterdaysDate", yesterdaysDate)

// Reading all the reports from the DataSource File
def reportType = context.expand( '${KPIReports#ReportType}' )

// Adding a condition based on reportType
if(reportType == "TOTALCUSTOMERS" || reportType == "MONTHENROLLMENTS" || reportType == "MONTHTRANSACTIONS" || reportType == "MONTHPOINTS" ||reportType == "DAILYPROGRAMSUMMARY")
{
	testRunner.gotoStepByName("GetKPIReportsNextYearDateNew")
} else
	testRunner.gotoStepByName("GetKPIReportsNextDayDateNew")
