con:config.script:
def fixedMomentCode = context.expand( '${GetKPIReports#Response#$[\'TopBonuses\'][0][\'BonusCode\']}')
def topBonusBeforeActivity = context.expand( '${GetKPIReportsTopBonus#Response#$[\'TopBonuses\'][?(@.BonusCode==\''+fixedMomentCode+'\')].PointsAmount[0]}' ).toBigDecimal()
def topBonusAfterActivity = context.expand( '${GetKPIReportsTopBonusAfterActivity#Response#$[\'TopBonuses\'][?(@.BonusCode==\''+fixedMomentCode+'\')].PointsAmount[0]}' ).toBigDecimal()

assert topBonusBeforeActivity <= topBonusAfterActivity
