con:config.script:
import static com.jayway.jsonpath.JsonPath.parse

def response = context.expand( '${Get Batch Job By Id#Response}' )

log.info response

if(response!=null && response!="null") {
	def status = parse(response).read('JobStatus')
	if(status!="C"&&status!="E") testRunner.gotoStepByName("Wait For Job Complete")
}

