con:config.script:
def AnswerId = context.expand( '${GetAnswers#Response#$[?(@.AnswerType=="Boolean")][\'AnswerId\'][0]}' )

if(AnswerId==null || AnswerId=="")
{
		testRunner.gotoStepByName("AddAnswer")

}
else
{
	   testRunner.testCase.setPropertyValue("AnswerId",AnswerId);
	    testRunner.gotoStepByName("GetQuestions")
}

def QuestionId = context.expand( '${GetQuestions#Response#$[0][\'QuestionId\']}' )

if(QuestionId==null || QuestionId=="")
{
	 testRunner.gotoStepByName("AddQuestion")

}
else
{
	testRunner.testCase.setPropertyValue("QuestionId",QuestionId)
	 testRunner.gotoStepByName("AddSurveyCategory")  
}
