def retry = "Get Logs"
int retries = 11
int curr = 1
sleep(10000)
def tRun = testRunner.runTestStepByName(retry)
while (tRun.getStatus().toString() != "PASS" && curr <= retries) {
	curr += 1
	sleep(10000)
     tRun = testRunner.runTestStepByName(retry)
}

if(tRun.getStatus().toString() != "PASS") {
	testRunner.fail("Test failed")
}

