con:config.script:
def ProfileId = context.expand( '${SearchProfileByCardNumberForPurTxn#Response#$[\'ProfileId\']}' )


if(ProfileId==null)
{
	testRunner.gotoStepByName( "AddProfileForPurTxn")
}
else
{
	testRunner.testCase.setPropertyValue("ProfileIdForPurTxn", ProfileId)
	testRunner.gotoStepByName( "UpdateProfileForPurTxn")
}

def TransactionNumber = context.expand( '${SearchPurchaseTransaction#Response#$[0][\'TransactionNumber\']}' )

if(TransactionNumber=="" || TransactionNumber==null)
{
	testRunner.gotoStepByName( "AddPurchaseTransaction")
}
else
{
	testRunner.gotoStepByName( "SearchProfileByReturnTransaction")
}

def TransactionNumber = context.expand( '${SearchAnonymousTransaction#Response$[\'TransactionNumber\']}' )

if(TransactionNumber==null || TransactionNumber=="")
{
testRunner.gotoStepByName( "AddAnonymousTranscation")
}
else
{
	testRunner.gotoStepByName( "SearchProfileByTxnNum_AnonymousTxn")
}

