con:config.script:
def segmentTypeId = context.expand( '${Get Segment Types#Response#$[?(@.SegmentTypeName=="${#TestCase#SEGMENT_TYPE}")][\'SegmentTypeId\']}' )

if (segmentTypeId == "" || segmentTypeId == "[]")
	testRunner.gotoStepByName("Add Segment Type")
else 
	testRunner.gotoStepByName("Get Segment Data")

def segmentdataresponse = context.expand( '${Get Segment Data#Response}' )

if (segmentdataresponse == "null")
	testRunner.gotoStepByName ("Add Segment Data")
else 
{
	def segmentdataId = context.expand( '${Get Segment Data#Response#$[?(@.SegmentTypeName=="${#TestCase#SEGMENT_TYPE}")][\'SegmentDataId\']}' )

	if(segmentdataId == "" || segmentdataId == "[]")
		testRunner.gotoStepByName ("Update Segment Data")
	else
		testRunner.gotoStepByName ("Add Segment Without Segment Type To Profile")
}
