import groovy.util.*
import groovy.json.*

def json = context.expand( '${GetELSegments#Response}' )
json = new groovy.json.JsonSlurper().parseText(json)
def count = json.size()

if (count >=10)
	testRunner.gotoStepByName("AddELSegmentExceedingLimit")
else 
	testRunner.gotoStepByName("Add EL Segment")

