con:config.script:
import groovy.json.*
import groovy.util.*

def scriptName = context.expand( '${#TestCase#ScriptName}' )
def filteredResponse = context.expand( '${GetProductionScript#Response#$[?(@.FusionWebApiName=="'+scriptName+'" || @.ScriptName=="'+scriptName+'")][0]}')
def jsonFilteredResponse = new JsonSlurper().parseText(filteredResponse)
def fusionWebApiId = jsonFilteredResponse.FusionWebApiId
def scriptStatus = jsonFilteredResponse.Status

if(fusionWebApiId == [])
 	testRunner.gotoStepByName("AddScriptByWebApiId")

if(scriptStatus=='A')
{
	def scriptId = jsonFilteredResponse.ScriptId
	testRunner.testCase.setPropertyValue('ScriptId', scriptId)
	testRunner.gotoStepByName("AddScriptByScriptId")
}
