def today = new Date()
//Integer  startDateSpecification = context.expand( '${#TestCase#StartDateSpecification}' ) as Integer
def StartDate = today - 2
log.info today.format("MM/dd/yyyy")
log.info StartDate.format("MM/dd/yyyy")
return StartDate.format("MM/dd/yyyy")

def today = new Date()
//Integer endDateSpecification = context.expand( '${#TestCase#EndDateSpecification}' ) as Integer
def EndDate = today + 1
log.info today.format("MM/dd/yyyy")
log.info EndDate.format("MM/dd/yyyy")
return EndDate.format("MM/dd/yyyy")

def offerresponse = context.expand( '${GetProgramOffers#Response}' )
log.info(offerresponse)
if(offerresponse =="null") 
{
	testRunner.runTestStepByName("AddProgramOffer");
	testRunner.gotoStepByName ("GetEventAttributesbyEventType");
}
else if(context.expand( '${GetProgramOffers#Response#$[0][\'Status\']}' ) != "A")  
{
	testRunner.gotoStepByName ("UpdateProgramOffer");
}
else  {
	testRunner.gotoStepByName ("GetEventAttributesbyEventType");
}

