log.info(Attribute);
if(Attribute == "[Get_Display_Offers.Profile.PreferredStoreCode]") 
{
	testRunner.gotoStepByName ("UpsertMoment");
}
else {
	testRunner.gotoStepByName("UpdateAttributePreferredStoreCodeInDisplayOffers");
