con:config.script:
def sysOfferId = context.expand( '${GetProgramOffers (Default Offer)#Response#$[0][\'SysOfferId\']}' )

if (sysOfferId == "")
   testRunner.gotoStepByName("AddProgramOffer")

else{
   testRunner.gotoStepByName("GetProgramOfferById")
   testRunner.testCase.setPropertyValue( "SysOfferId", sysOfferId )
}
