//check if any program offer exists 
def sysOfferId = context.expand( '${GetProgramOffer#Response#$[0][\'SysOfferId\']}' )

if (sysOfferId == "")
 testRunner.gotoStepByName("AddProgramOffer")
  else 
    testRunner.gotoStepByName("UpdateProgramOffer")

def sysOfferId = context.expand( '${GetProgramOffer#Response#$[0][\'SysOfferId\']}' )

if (sysOfferId == "")
	sysOfferId = context.expand( '${AddProgramOffer#Response#$[\'SysOfferId\']}' )
else
	sysOfferId = context.expand(  '${UpdateProgramOffer#Response#$[\'SysOfferId\']}'  )
return sysOfferId;

def isDefaultOffer = context.expand( '${AIQOffers#IsDefaultOffer}' )
log.info(isDefaultOffer)
if(isDefaultOffer == "TRUE")
	 testRunner.gotoStepByName("AIQOffersLoop")
else
	testRunner.gotoStepByName("AddOfferToProfile")

import java.util.Date

def transDate = new java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ssZ").format(new Date())
def year = new Date().getYear() + 1900
def transNumber = context.expand( '${AIQTransactions#TransNumber}' )
def month =  (new Date().getMonth() + Integer.parseInt(transNumber))

if(month > 12)
	month = month-12

return  year.toString() + '-' +month.toString() + '-' +  new Date().getDate()
