import java.util.Date

def transDate = new java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ssZ").format(new Date())
def year = new Date().getYear() + 1900
def transNumber = context.expand( '${AIQTransactions#TransNumber}' )
def month =  (new Date().getMonth() + Integer.parseInt(transNumber))

if(month > 12)
	month = month-12
	year++

return  year.toString() + '-' +month.toString() + '-' +  new Date().getDate()
