import static com.jayway.jsonpath.JsonPath.parse
import java.text.DateFormat;
import java.text.SimpleDateFormat;
import java.time.format.DateTimeFormatter
import java.time.LocalDateTime;

def statusCode = context.expand( '${#[OffersPrerequisite#GetProgramOffers#GetProgramOffers]#HarResponse#$[\'status\']}' ).toInteger()
def OfferCode = context.expand( '${#[OffersPrerequisite#GetProgramOffers#GetProgramOffers]#Response#$[0][\'OfferCode\']}' )
log.info(statusCode)
log.info(OfferCode)

def futureDate = LocalDateTime.now().plusMonths(6).format(DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss.SSS"));
log.info(futureDate)

if (statusCode == 200 && OfferCode == "")  
	testRunner.gotoStepByName("AddAnEligibleProgramOffer")
else  
	testRunner.gotoStepByName("Update Program Offer")

return futureDate

