con:config.script:
def statusCode = context.expand( '${#[ProgramOffers#GetSysOfferId#Get Offer Details]#HarResponse#$[\'status\']}' ).toInteger()

def offerStatus = context.expand( '${#[ProgramOffers#GetSysOfferId#Get Offer Details]#Response#$[0][\'Status\']}' )


if (statusCode == 200 && offerStatus == "A"){
	testRunner.gotoStepByName("AddOfferToProfile")
}

else {
	testRunner.gotoStepByName("UpdateSysOfferStatus")
}
