import static com.jayway.jsonpath.JsonPath.parse
def programCode= context.expand( '${BasicSettings#ProgramCode}' )
log.info programCode
if (programCode == "PP" )  testRunner.gotoStepByName("GetPostingKeys")
else  {
testRunner.gotoStepByName("GetTenderTypes3")
}

import static com.jayway.jsonpath.JsonPath.parse

def postingKeyCode = context.expand( '${GetPostingKeys#Response#$.[0].PostingKeyCode}' )
log.info postingKeyCode
if (postingKeyCode == "PK1239" ) {
testRunner.gotoStepByName("AddProfileWithPostingKey")
}
else if (postingKeyCode == null || postingKeyCode == "" ) {
testRunner.gotoStepByName("AddProfile")
}

import static com.jayway.jsonpath.JsonPath.parse


def response = context.expand( '${AddProfileWithPostingKey#Response#$.ProfileId}' )
log.info response
if(response == "" || response == null) {
	response = context.expand( '${AddProfile#Response#$.ProfileId}' )
	log.info response
}

context.testCase.setPropertyValue('ProfileId',response)

