import groovy.json.*
import groovy.util.*
import groovy.lang.*

def linkTableName = context.expand( '${RVCSegments#LinkTableName}' )
def linkColumnName = context.expand( '${RVCSegments#LinkColumnName}' )

def filteredResponse = context.expand( '${GetRVCSegmentDataLink#Response#$[?(@.LinkTableName=="'+linkTableName+'" && @.LinkColumnName=="'+linkColumnName+'")][0]}' )
def jsonFilteredResponse = new JsonSlurper().parseText(filteredResponse)
def segmentDataLinkId = jsonFilteredResponse.SegmentDataLinkId

testRunner.testCase.setPropertyValue('SegmentDataLinkId', segmentDataLinkId)
