con:config.script:
def checkStatusCustomerStrategy = context.expand( '${GetProgramSettingForCustomerStrategy#Response#$[0][\'Status\']}' );
if(checkStatusCustomerStrategy == "") checkStatusCustomerStrategy = 'I';
def checkStatusOverviewEL = context.expand( '${GetProgramSettingForOVERVIEW_EL#Response#$[0][\'Status\']}' );
if(checkStatusOverviewEL == "") checkStatusOverviewEL = 'I';
def checkStatusOverviewVAP = context.expand( '${GetProgramSettingForOVERVIEW_VAP#Response#$[0][\'Status\']}' );
if(checkStatusOverviewVAP == "") checkStatusOverviewVAP = 'I';
def checkStatusOverviewRVC = context.expand( '${GetProgramSettingForOVERVIEW_RVC#Response#$[0][\'Status\']}' );
if(checkStatusOverviewRVC == "") checkStatusOverviewRVC = 'I';

if(checkStatusCustomerStrategy == 'A' && (checkStatusOverviewEL == 'A'  && checkStatusOverviewVAP == 'A' && checkStatusOverviewRVC == 'A')){
	testRunner.runTestStepByName("UpdateProgramSettingsToY");
	testRunner.runTestStepByName("ValidationForDuplicateDisplayOrder");
	testRunner.runTestStepByName("ValidationForInvalidParameterName");
	testRunner.runTestStepByName("ValidationsForExistingNameWithDisplayOrderN");
	testRunner.runTestStepByName("ValidationsForExistingNameWithDisplayOrderY");
	testRunner.runTestStepByName("ValidationsForExistingNameWithDisplayOrderBlankY");
	testRunner.runTestStepByName("ValidationsForExistingNameWithDisplayOrderBlankN");
	testRunner.runTestStepByName("ValidationsForExistingNameWithoutDisplayOrderN");
	testRunner.runTestStepByName("ValidationsForExistingNameWithoutDisplayOrderY");
	testRunner.runTestStepByName("ValidationForDuplicateCheckN");
	testRunner.runTestStepByName("ValidationForParentDisplayOrder");
	testRunner.runTestStepByName("ValidationForDisablingParentField");
	testRunner.runTestStepByName("ValidationForNegativeDisplayOrder");
	testRunner.runTestStepByName("ValidationForMultipleUpdatesWithParentAsN");
	testRunner.runTestStepByName("ValidationForMultipleUpdatesWithParentAsY");
	testRunner.runTestStepByName("ValidationForMultipleUpdateWithParentChildAsY");
	testRunner.runTestStepByName("ValidationForMultipleUpdatesWithParentChildWithInvalidChild");
	testRunner.runTestStepByName("ValidationForChildParameterValue");
	testRunner.runTestStepByName("ValidationForParentParameterValue");
	testRunner.runTestStepByName("ValidationForParentChildToN");
	testRunner.runTestStepByName("ValidationForParentToYAndChildsToN");
	testRunner.runTestStepByName("ValidationForParentToYAndOneChildToY");
	testRunner.runTestStepByName("ValidationForParentToNAndOneChildToY");
	testRunner.runTestStepByName("UpdateProgramSettingToDefault");
	testRunner.gotoStepByName("GetProgramSetting");
}
else if(checkStatusCustomerStrategy == 'A' && (checkStatusOverviewEL != 'A'  || checkStatusOverviewVAP != 'A'  || checkStatusOverviewRVC != 'A'))
{
	testRunner.runTestStepByName("ValidationForParentUpdateWithoutDisplayOrderY");
	testRunner.runTestStepByName("ValidationForParentUpdateWithDisplayOrderY");
	testRunner.runTestStepByName("ValidationForParentUpdateWithDisplayOrderN");
	testRunner.runTestStepByName("ValidationForParentUpdateWithInvalidParameterName");
	testRunner.runTestStepByName("ValidationForParentUpdateWithChildsN");
	testRunner.gotoStepByName("GetProgramSetting");
}
else if(checkStatusCustomerStrategy == 'I' && (checkStatusOverviewEL == 'A'  || checkStatusOverviewVAP == 'A'  || checkStatusOverviewRVC == 'A'))
{
	testRunner.runTestStepByName("ValidationForParentInActive&ChildsActive");
	testRunner.gotoStepByName("GetProgramSetting");
}
else{
		testRunner.runTestStepByName("GetProgramSetting");
}
