import static com.jayway.jsonpath.JsonPath.parse

//Get the parsed json of GetSources test step response
def json = new groovy.json.JsonSlurper().parseText(context.expand('${GetSystemRoles#Response}'))

//datasource
def RolesDatasource =context.expand( '${Roles#RoleCode}')

def getRoleIdByRoleCode = { code ->  json.find {it?.RoleCode == RolesDatasource}?.RoleId }

def RoleId= (getRoleIdByRoleCode('RolesDatasource'))

if ( RoleId == "" || RoleId== null ) 
    testRunner.gotoStepByName("AddSystemRole")
else{
	testRunner.gotoStepByName("RoleId")
}

import static com.jayway.jsonpath.JsonPath.parse

//Get the parsed json of GetSources test step response
def json = new groovy.json.JsonSlurper().parseText(context.expand('${GetSystemRoles#Response}'))

//datasource
def RolesDatasource =context.expand( '${Roles#RoleCode}')

def getRoleIdByRoleCode = { code ->  json.find {it?.RoleCode == RolesDatasource}?.RoleId }

def RoleId= (getRoleIdByRoleCode('RolesDatasource'))

def response = context.expand( '${AddSytemRole#Response}' )

if(response==null || response =="")
  	return RoleId;

 else
 {
    RoleId = context.expand( '${AddSystemRole#Response#$[\'RoleId\']}' )
    return RoleId;
 }
