import static com.jayway.jsonpath.JsonPath.parse

//Get the parsed json of GetSources test step response
def json = new groovy.json.JsonSlurper().parseText(context.expand('${GetSystemUsers#Response}'))

//datasource
def UserDatasource =context.expand( '${Users#Username}')

//username
def GetUsername = { code ->  json.find {it?.Username == UserDatasource}?.Username}

def username= (GetUsername('UserDatasource'))

if ( username == "" || username== null ) 
	  testRunner.gotoStepByName("GetSystemRoles")

else{
	testRunner.gotoStepByName("UsersLoop")
}

import static com.jayway.jsonpath.JsonPath.parse

//Get the parsed json of GetSources test step response
def json = new groovy.json.JsonSlurper().parseText(context.expand('${GetSystemRoles#Response}'))

//datasource
def UserDatasource =context.expand( '${Users#RoleCode}')

//
def GetRoleCodeByRoleId = { code ->  json.find {it?.RoleCode == UserDatasource}?.RoleId}

def RoleCode= (GetRoleCodeByRoleId('UserDatasource'))

return RoleCode
