con:config.script:
def target = testRunner.testCase
def source = testRunner.testCase.testSteps["Defaults"]
for(prop in source.getPropertyList()) {
	assignDefault(target, source, prop.getName())
}

def assignDefault(target, source, propName) {
	def prop = target.getPropertyValue(propName)
	if(prop == "" || prop == null)
		target.setPropertyValue(propName, source.getPropertyValue(propName))
}
