import static com.jayway.jsonpath.JsonPath.parse
//disable profile required fields
def reqFieldCode = context.expand( '${GetProfileRequiredFields#Response#$[0][\'RequiredFieldCode\']}' )
if (reqFieldCode != "" ) 
    testRunner.gotoStepByName("UpdateProfileRequiredFields")
    else 
      testRunner.gotoStepByName("GetProgramSettings")

import static com.jayway.jsonpath.JsonPath.parse

def accountPoolingValue = context.expand( '${GetProgramSettings#Response#$[0][\'ParameterValue\']}' )
if (accountPoolingValue == "N" ) 
    testRunner.gotoStepByName("UpdateAccPoolProgramSetting")
    else 
     testRunner.gotoStepByName("UpdateProgramSettings")
