def generator = { String alphabet, int n ->
  new Random().with {
    (1..n).collect { alphabet[ nextInt( alphabet.length() ) ] }.join()
  }
}

OfferCode = generator( (('A'..'Z')+('0'..'9')).join(), 9 )
OfferCode = "600_EGGS_RGRREDEEM" + OfferCode
return OfferCode
