con:configuration.scriptText:
import groovy.json.JsonSlurper

// Get the PointSubTypeId
def pointSybTypeId = context.expand( '${GetPointTypeById#Response#$[\'PointSubTypes\'][?(@.PointSubTypeCode==\'HATCHED\')].PointSubTypeId[0]}' )

// To get all the balance points
//def allBalancePoints = context.expand( '${GetPointsSummary#Response#$[?(@.PointSubTypeId==\''+ pointSybTypeId +'\' && @.ActivityType==\'REWARD\')].BalancePoints}' )

//Get the Activity Type
def activityType="REWARD"

//Get the Points Summary Response
def response = context.expand( '${GetPointsSummary#Response}' )

// Parse the  Points Summary Response  with JsonSlurper
def pointsSummary = new JsonSlurper().parseText( response )

// Loop through the json array, and add the balance points
def balancePoints = new BigDecimal(0)
pointsSummary.each{val ->
               if(val.PointSubTypeId == pointSybTypeId && val.ActivityType == activityType) {
                                             balancePoints = balancePoints + val.BalancePoints.toBigDecimal()
               }
}

assert balancePoints ==2000
