con:soapui-project.con:beforeSaveScript:
/**
* Project level's Save Script
* Cleans up all the custom properties 
* If CLEAN_PROPERTIES value of project property is set to true
* And save the project
*
*/

def ignoreTestCaseProperties = [ ] 
def ignoreTestSuiteProperties = [ ] 
def ignoreProjectProperties = ['CLEAN_PROPERTIES'] 
def cleanProperties = 
{ model, list -> model.properties.keySet().findAll{ !(it in list) 
} each { tProp -> model.setPropertyValue(tProp,'') } }
if ('true' == project.getPropertyValue('CLEAN_PROPERTIES'))
 {
	cleanProperties(project, ignoreProjectProperties)	
	project.setPropertyValue('CLEAN_PROPERTIES', 'false') 
}

con:eventHandlers.con:script:
// Sample event script to add custom HTTP header to all outgoing REST, SOAP and HTTP(S) calls
      // This code is often used for adding custom authentication to SoapUI Pro tests

      // If hardcoding the token, uncomment and change line 5
      // def token = '4567'

      // If your token is parameterized in Project level custom property, uncomment line 8
      // def token = request.parent.testCase.testSuite.project.getProperty('auth_token').getValue()

      // To modify all outgoing calls, remove comments from lines 11 to 16
      // def headers = request.requestHeaders
      // if (headers.containsKey('auth_token2') == false) {
      //   headers.put('auth_token2', token)
      //   request.requestHeaders = headers
      // }

// Save all test step results into files
      // Change the directory path in line 5 to a location where you want to store details
      // then uncomment lines 5 to 10

      // filePath = 'C:\\tempOutputDirectory\\'
      // fos = new FileOutputStream(filePath + testStepResult.testStep.label + '.txt', true)
      // pw = new PrintWriter(fos)
      // testStepResult.writeTo(pw)
      // pw.close()
      // fos.close()

def authToken = null
      def accessTokenExpiration = null
      def testCaseName = 'CreateToken'
      def testStepName = 'CreateToken'
      def prj = testRunner.testCase.testSuite.project
      tCase = prj.testSuites['LoadToken'].testCases[testCaseName]
      def expirationDate = null
      def tokenExpired = true

      try {
      authToken = testRunner.testCase.testSuite.project.getPropertyValue( "AuthToken" )
      accessTokenExpiration = testRunner.testCase.testSuite.project.getPropertyValue( "AccessTokenExpiration" )
      try {
      expirationDate = new java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss.S'Z'").parse(accessTokenExpiration)
      tokenExpired =  (new Date()  >= expirationDate)
      }
      catch (Exception e) {
      tokenExpired = true
      }

      if (authToken == null || authToken == '' || tokenExpired) {
      tStep = tCase.getTestStepByName(testStepName)
      tStep.run(testRunner, context)
      def response = tStep.testRequest.testStep.testRequest.response.responseContent
      def parsedResponse = new groovy.json.JsonSlurper().parseText(response.toString())
      def newAccessToken = parsedResponse.AccessToken
      testRunner.testCase.testSuite.project.setPropertyValue( "AuthToken", newAccessToken )
      testRunner.testCase.testSuite.project.setPropertyValue( "AccessTokenExpiration", parsedResponse.AccessTokenExpiration )
      }

      } catch (Exception e) {
      log.info e.Message()
      }
